function roundUp(e,t){return Math.round(e*Math.pow(10,t))/Math.pow(10,t)}function updateBitcoinPrice(e){[...$a(".pricing .pricing-item")].forEach((function(t){const n=t.querySelector(".fiat .currency-value").innerText,c=Math.round(n/e*1e8),r=1e-8*c;t.querySelector(".bitcoin a").href+="?amount="+r;const i=t.querySelector(".bitcoin .currency-value"),o=.01*c;i.innerText=roundUp(o,2),i.classList.remove("currency-loading")}))}function updateIOTAPrice(e){[...$a(".pricing .pricing-item")].forEach((function(t){const n=t.querySelector(".fiat .currency-value").innerText,c=t.querySelector(".iota .currency-value");c.innerText=roundUp(n/e,3),c.classList.remove("currency-loading")}))}function fetchUpdate(){fetch("https://blockchain.info/ticker").then(e=>e.json()).then(e=>updateBitcoinPrice(e.EUR["15m"])),fetch("https://iota-price.herokuapp.com/").then(e=>e.json()).then(e=>updateIOTAPrice(e.euro))}document.addEventListener("DOMContentLoaded",(function(){setInterval(fetchUpdate(),3e5)}));
